class OpenHolidays{constructor(){this.baseUrl="https://openholidaysapi.org"}async getPublicHolidays(countryIsoCode,validFrom,validTo,languageIsoCode="DE",acceptHeader="application/json"){const url=`${this.baseUrl}/PublicHolidays?countryIsoCode=${countryIsoCode.toUpperCase()}&languageIsoCode=${languageIsoCode.toUpperCase()}&validFrom=${validFrom}&validTo=${validTo}`;return this.call(url,acceptHeader)}async getSchoolHolidays(countryIsoCode,subdivisionCode,validFrom,validTo,languageIsoCode="DE",acceptHeader="application/json"){const url=`${this.baseUrl}/SchoolHolidays?countryIsoCode=${countryIsoCode.toUpperCase()}&subdivisionCode=${subdivisionCode.toUpperCase()}&languageIsoCode=${languageIsoCode.toUpperCase()}&validFrom=${validFrom}&validTo=${validTo}`;return this.call(url,acceptHeader)}async getCountries(languageIsoCode="DE",acceptHeader="application/json"){const url=`${this.baseUrl}/Countries?languageIsoCode=${languageIsoCode}`;return this.call(url,acceptHeader)}async getLanguages(languageIsoCode="DE",acceptHeader="application/json"){const url=`${this.baseUrl}/Languages?languageIsoCode=${languageIsoCode}`;return this.call(url,acceptHeader)}async getSubdivisions(countryIsoCode="DE",languageIsoCode="DE",acceptHeader="application/json"){const url=`${this.baseUrl}/Subdivisions?languageIsoCode=${languageIsoCode}&countryIsoCode=${countryIsoCode}`;return this.call(url,acceptHeader)}async call(url,acceptHeader){const options={method:"GET",headers:{Accept:acceptHeader}};try{const response=await fetch(url,options);if(!response.ok){throw new Error(`API-Anfrage fehlgeschlagen: ${response.statusText}, HTTP-Code: ${response.status}`)}if(acceptHeader==="application/json"){return await response.json()}return await response.text()}catch(error){throw new Error(`API-Anfrage fehlgeschlagen: ${error.message}`)}}}